import { useForm } from "react-hook-form";
import { useState, useRef, useEffect } from "react";
import axios from "axios";

const Form = () => {
  const {
    register,
    formState: { errors, isValid },
    handleSubmit,
    reset,
  } = useForm({
    mode: "onBlur",
  });

  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");

  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const phoneRef = useRef(null);
  const commentsRef = useRef(null);


  // async function sendMail() {
    //   try {
      //     await axios
      //       .post("http://localhost:5000/send_email", {
        //         fullName,
        //         email,
        //         phone,
        //         message,
        //       });
        //     return console.log("Send succesfully");
        //   } catch (error) {
          //     return console.log("error", error);
          //   }
          // }

          useEffect(() => {
            nameRef.current = document.getElementById("name");
            emailRef.current = document.getElementById("email");
            phoneRef.current = document.getElementById("phone");
            commentsRef.current = document.getElementById("comments");
          }, []);
  const onSubmit = async() => {
    const data = {
      companyName: nameRef.current.value,
      email: emailRef.current.value,
      phone: phoneRef.current.value,
      comments: commentsRef.current.value,
    };

    try {
      const response = await axios
        .post("http://localhost:5000/send_email", data)

          console.log(response);
          reset();
    }
        catch(error) {
          console.log(error);
        };

  }

  return (
    <section className="form" id="cooperate">
      <div className="container">
        <div className="form__background">
          <h3 className="heading heading--white">Let's cooperate!</h3>
          <form
            name="form"
            id="form"
            className="form__body"
            action="#"
            onSubmit={handleSubmit(onSubmit)}
          >
            <div className="input__wrapper">
              <input
                ref={nameRef}
                defaultValue={fullName}
                type="text"
                name="name"
                onChange={(e) => setFullName(e.target.value)}
                className={`form__item ${errors.companyName && "error"} ${
                  !errors.companyName && isValid.companyName && "accepted"
                }`}
                placeholder="Company Name*"
                {...register("companyName", {
                  required: "Please enter your name",
                  minLength: {
                    value: 3,
                    message: "Minimum 3 characters",
                  },
                  pattern: "^[a-zA-Z0-9s]+$",
                })}
              />
              <div className="form__error">
                {errors?.companyName && (
                  <p className="form__error-text">
                    {errors?.companyName?.message || "Error"}
                  </p>
                )}
              </div>
            </div>
            <div className="input__wrapper">
              <input
                ref={emailRef}
                type="email"
                name="email"
                defaultValue={email}
                onChange={(e) => setEmail(e.target.value)}
                className={`form__item ${errors.companyMail && "error"} ${
                  !errors.companyMail && isValid.companyMail && "accepted"
                }`}
                placeholder="E-mail*"
                {...register("companyMail", {
                  required: "Please enter your e-mail",
                  minLength: {
                    value: 5,
                    message: "Minimum 5 characters",
                  },
                  pattern: "^[-w.]+@([A-z0-9][-A-z0-9]+.)+[A-z]{2,4}$",
                })}
              />
              <div className="form__error">
                {errors?.companyMail && (
                  <p className="form__error-text">
                    {errors?.companyMail?.message || "Error"}
                  </p>
                )}
              </div>
            </div>
            <div className="input__wrapper">
              <input
                ref={phoneRef}
                type="text"
                name="phone"
                defaultValue={phone}
                onChange={(e) => setPhone(e.target.value)}
                className={`form__item ${errors.companyPhone && "error"} ${
                  !errors.companyPhone && isValid.companyPhone && "accepted"
                }`}
                placeholder="Phone*"
                {...register("companyPhone", {
                  required: "Please enter your phone",
                  minLength: {
                    value: 5,
                    message: "Minimum 5 characters",
                  },
                  pattern:
                    "^(+)?((d{2,3}) ?d|d)(([ -]?d)|( ?(d{2,3}) ?)){5,12}d$",
                })}
              />
              <div className="form__error">
                {errors?.companyPhone && (
                  <p className="form__error-text">
                    {errors?.companyPhone?.message || "Error"}
                  </p>
                )}
              </div>
            </div>
            <div className="input__wrapper">
              <input
                ref={commentsRef}
                type="text"
                name="comments"
                defaultValue={message}
                onChange={(e) => setMessage(e.target.value)}
                className="form__item"
                placeholder="Comments"
              />
            </div>
            <input
              type="submit"
              value="Contact Us"
              className="hvr-push button--submit"
              id="button"
              name="button"
              // onClick={() => sendMail()}
              disabled={!isValid}
            />
          </form>
          <div className="form__popup">
            <div className="form__popup-wrapper">
              <img src="img/done.svg" alt="" className="form__popup-image" />
              <div className="form__popup-message">
                <h3 className="form__popup-heading">
                  Thanks for your submission!
                </h3>
                <p className="form__popup-text">
                  We'll get back to you as soon as possible
                </p>
              </div>
              <button className="ok" id="close">
                OK
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Form;
